import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, Paper, CircularProgress } from '@mui/material';

const Configuration = () => {
    const [serviceNowUrl, setServiceNowUrl] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchConfig();
    }, []);

    const fetchConfig = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get('http://localhost:5000/api/config'); // ✅ Correct API route
            if (response.data.service_now_url) {
                setServiceNowUrl(response.data.service_now_url);
            }
        } catch (error) {
            console.error('Error fetching configuration:', error);
            setError('Failed to fetch configuration.');
        } finally {
            setLoading(false);
        }
    };

    const handleSave = async () => {
        setLoading(true);
        setError(null);
        try {
            await axios.post('http://localhost:5000/api/config', { service_now_url: serviceNowUrl }); // ✅ Correct field name
            alert('Configuration updated successfully!');
        } catch (error) {
            console.error('Error updating configuration:', error);
            setError('Failed to update configuration.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container>
            <Paper elevation={3} style={{ padding: 20, maxWidth: 500, margin: '20px auto' }}>
                <Typography variant="h5" gutterBottom>
                    ServiceNow Configuration
                </Typography>
                
                {loading ? <CircularProgress /> : null}

                {error && (
                    <Typography color="error" variant="body2" style={{ marginBottom: '10px' }}>
                        {error}
                    </Typography>
                )}

                <TextField
                    label="ServiceNow URL"
                    fullWidth
                    value={serviceNowUrl}
                    onChange={(e) => setServiceNowUrl(e.target.value)}
                    margin="normal"
                />
                
                <Button variant="contained" color="primary" onClick={handleSave} disabled={loading}>
                    Save
                </Button>
            </Paper>
        </Container>
    );
};

export default Configuration;
