import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, Paper } from '@mui/material';

const Configuration = () => {
    const [serviceNowUrl, setServiceNowUrl] = useState('');

    useEffect(() => {
        fetchConfig();
    }, []);

    const fetchConfig = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/config'); // Adjust endpoint if needed
            if (response.data.serviceNowUrl) {
                setServiceNowUrl(response.data.serviceNowUrl);
            }
        } catch (error) {
            console.error('Error fetching configuration:', error);
        }
    };

    const handleSave = async () => {
        try {
            await axios.post('http://localhost:5000/api/config', { serviceNowUrl });
            alert('Configuration updated successfully!');
        } catch (error) {
            console.error('Error updating configuration:', error);
            alert('Failed to update configuration.');
        }
    };

    return (
        <Container>
            <Paper elevation={3} style={{ padding: 20, maxWidth: 500, margin: '20px auto' }}>
                <Typography variant="h5" gutterBottom>
                    ServiceNow Configuration
                </Typography>
                <TextField
                    label="ServiceNow URL"
                    fullWidth
                    value={serviceNowUrl}
                    onChange={(e) => setServiceNowUrl(e.target.value)}
                    margin="normal"
                />
                <Button variant="contained" color="primary" onClick={handleSave}>
                    Save
                </Button>
            </Paper>
        </Container>
    );
};

export default Configuration;
