import React, { useState, useEffect } from "react";
import { Container, Typography, Select, MenuItem, Button, Grid } from "@mui/material";
import axios from "axios";

const Mapping = () => {
  const [serviceNowFields, setServiceNowFields] = useState([]);
  const [dbFields, setDbFields] = useState([]);
  const [mappings, setMappings] = useState({});

  useEffect(() => {
    // Fetch fields from ServiceNow and database
    axios.get("http://localhost:3833/api/servicenow/fields").then((response) => {
      setServiceNowFields(response.data);
    });

    axios.get("http://localhost:3833/api/database/fields").then((response) => {
      setDbFields(response.data);
    });
  }, []);

  const handleMappingChange = (snField, dbField) => {
    setMappings((prev) => ({ ...prev, [snField]: dbField }));
  };

  const handleSaveMappings = () => {
    axios.post("http://localhost:3833/api/mappings", mappings).then((response) => {
      alert("Mappings saved successfully!");
    });
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Field Mapping
      </Typography>
      <Grid container spacing={2}>
        {serviceNowFields.map((snField) => (
          <Grid item xs={12} sm={6} key={snField}>
            <Typography>{snField}</Typography>
            <Select
              fullWidth
              value={mappings[snField] || ""}
              onChange={(e) => handleMappingChange(snField, e.target.value)}
            >
              <MenuItem value="">Select Database Field</MenuItem>
              {dbFields.map((dbField) => (
                <MenuItem key={dbField} value={dbField}>
                  {dbField}
                </MenuItem>
              ))}
            </Select>
          </Grid>
        ))}
      </Grid>
      <Button variant="contained" color="primary" onClick={handleSaveMappings} style={{ marginTop: "20px" }}>
        Save Mappings
      </Button>
    </Container>
  );
};

export default Mapping;
