import React, { useState, useEffect } from "react";
import { Box, Container, Typography, TextField, Button, Card, CardContent } from "@mui/material";
import axios from "axios";

const Configuration = () => {
  const [serviceNowUrl, setServiceNowUrl] = useState("");
  const [savedUrl, setSavedUrl] = useState("");

  useEffect(() => {
    // Fetch current ServiceNow URL from backend
    axios.get("http://localhost:3833/api/configuration")
      .then((response) => setSavedUrl(response.data.url))
      .catch((error) => console.error("Error fetching URL:", error));
  }, []);

  const handleSave = () => {
    axios.post("http://localhost:3833/api/configuration", { url: serviceNowUrl })
      .then(() => {
        setSavedUrl(serviceNowUrl);
        setServiceNowUrl("");
      })
      .catch((error) => console.error("Error saving URL:", error));
  };

  return (
    <Container maxWidth="sm">
      <Box mt={4}>
        <Card>
          <CardContent>
            <Typography variant="h5" gutterBottom>Configuration</Typography>
            <Typography variant="body1">Current ServiceNow URL: {savedUrl || "Not Set"}</Typography>
            <TextField
              fullWidth
              label="Enter ServiceNow URL"
              value={serviceNowUrl}
              onChange={(e) => setServiceNowUrl(e.target.value)}
              margin="normal"
            />
            <Button variant="contained" color="primary" onClick={handleSave}>
              Save URL
            </Button>
          </CardContent>
        </Card>
      </Box>
    </Container>
  );
};

export default Configuration;
